import React, { useState, useEffect } from 'react';

const ERROR_IMG_SRC =
  'data:image/svg+xml;base64,';

interface ImageWithFallbackProps {
  src?: string;
  alt?: string;
  className?: string;
  fallbackSrc?: string;
  [key: string]: any;
}

export function ImageWithFallback(props: ImageWithFallbackProps) {
  const { src, fallbackSrc, alt, className, ...rest } = props;

  const [imgSrc, setImgSrc] = useState(src);

  useEffect(() => {
    setImgSrc(src);
  }, [src]);

  const handleError = () => {
    setImgSrc(fallbackSrc || ERROR_IMG_SRC);
  };

  return (
    <img
      src={imgSrc || ERROR_IMG_SRC}
      alt={alt || ''}
      className={className}
      onError={handleError}
      {...rest}
    />
  );
}
